var keys=document.querySelectorAll(".key");var sounds = document.querySelectorAll("audio");keys.forEach(key=>{key.addEventListener("click",()=>{playSound(key)})});document.addEventListener("keypress",(e)=>{keys.forEach(key=>{if(key.dataset.key==e.key){playSound(key)};})});function playSound(key){sounds.forEach(sound=>{if(key.dataset.key==sound.dataset.key){const audio=new Audio(sound.src);audio.play();};})}
